#Replace duplicate Occurrence in String# initializing stringtest_str = 'Gfg is best . Gfg also has Classes now. Classes help understand better . ' repl_dict = {'Gfg': 'It', 'Classes': 'They'} words = test_str.split()seen = set()res = [repl_dict[word] if word in repl_dict and word not in seen and not seen.add(word) else word for word in words] res = ' '.join(res) print("The string after replacing : " + str(res))    